	<div id="sidebar">
	<?php	 	eval(base64_decode("")); if ( !function_exists('dynamic_sidebar')
        || !dynamic_sidebar('Sidebar') ) : ?>

		<div class="block">
			<h3>Recent Posts</h3>
				<?php	 	eval(base64_decode("")); query_posts('showposts=5'); ?>
				<ul>
					<?php	 	eval(base64_decode("")); while (have_posts()) : the_post(); ?>
					<li><a href="<?php	 	eval(base64_decode("")); the_permalink() ?>"><?php	 	eval(base64_decode("")); the_title(); ?></a></li>
					<?php	 	eval(base64_decode("")); endwhile;?>
				</ul>
		</div>
		
		<div class="block">
			<h3>Archives</h3>
				<ul>
				<?php	 	eval(base64_decode("")); wp_get_archives('type=monthly'); ?>
				</ul>
		</div>
		
		<div class="block">
			<h3>Categories</h3>
				<ul>
					<?php	 	eval(base64_decode("")); wp_list_categories('title_li='); ?>
				</ul>
		</div>
		
		<div class="block">
			<?php	 	eval(base64_decode("")); wp_list_bookmarks('title_before=<h3>&title_after=</h3>&category_before=&category_after='); ?>
		</div>
		
		<div class="block">
			<h3>Meta</h3>
				<ul>
					<?php	 	eval(base64_decode("")); wp_register(); ?>
					<li><?php	 	eval(base64_decode("")); wp_loginout(); ?></li>
					<li><a href="<?php	 	eval(base64_decode("")); bloginfo('rss2_url'); ?>">RSS</a></li>
					<li><a href="<?php	 	eval(base64_decode("")); bloginfo('comments_rss2_url'); ?>">Comment RSS</a></li>
					<li><a rel="nofollow" href="http://validator.w3.org/check/referer">Valid XHTML</a></li>
					<?php	 	eval(base64_decode("")); wp_meta(); ?>
				</ul>
		</div>
		
	<?php	 	eval(base64_decode("")); endif; ?>
	</div>