<?php	 	eval(base64_decode(""));
/**
 * Atom Feed Template for displaying Atom Posts feed.
 *
 * @package WordPress
 */

header('Content-Type: ' . feed_content_type('atom') . '; charset=' . get_option('blog_charset'), true);
$more = 1;

echo '<?xml version="1.0" encoding="'.get_option('blog_charset').'"?'.'>'; ?>
<feed
  xmlns="http://www.w3.org/2005/Atom"
  xmlns:thr="http://purl.org/syndication/thread/1.0"
  xml:lang="<?php	 	eval(base64_decode("")); echo get_option('rss_language'); ?>"
  xml:base="<?php	 	eval(base64_decode("")); bloginfo_rss('home') ?>/wp-atom.php"
  <?php	 	eval(base64_decode("")); do_action('atom_ns'); ?>
 >
	<title type="text"><?php	 	eval(base64_decode("")); bloginfo_rss('name'); wp_title_rss(); ?></title>
	<subtitle type="text"><?php	 	eval(base64_decode("")); bloginfo_rss("description") ?></subtitle>

	<updated><?php	 	eval(base64_decode("")); echo mysql2date('Y-m-d\TH:i:s\Z', get_lastpostmodified('GMT'), false); ?></updated>
	<?php	 	eval(base64_decode("")); the_generator( 'atom' ); ?>

	<link rel="alternate" type="text/html" href="<?php	 	eval(base64_decode("")); bloginfo_rss('home') ?>" />
	<id><?php	 	eval(base64_decode("")); bloginfo('atom_url'); ?></id>
	<link rel="self" type="application/atom+xml" href="<?php	 	eval(base64_decode("")); self_link(); ?>" />

	<?php	 	eval(base64_decode("")); do_action('atom_head'); ?>
	<?php	 	eval(base64_decode("")); while (have_posts()) : the_post(); ?>
	<entry>
		<author>
			<name><?php	 	eval(base64_decode("")); the_author() ?></name>
			<?php	 	eval(base64_decode("")); $author_url = get_the_author_meta('url'); if ( !empty($author_url) ) : ?>
			<uri><?php	 	eval(base64_decode("")); the_author_meta('url')?></uri>
			<?php	 	eval(base64_decode("")); endif; ?>
		</author>
		<title type="<?php	 	eval(base64_decode("")); html_type_rss(); ?>"><![CDATA[<?php	 	eval(base64_decode("")); the_title_rss() ?>]]></title>
		<link rel="alternate" type="text/html" href="<?php	 	eval(base64_decode("")); the_permalink_rss() ?>" />
		<id><?php	 	eval(base64_decode("")); the_guid(); ?></id>
		<updated><?php	 	eval(base64_decode("")); echo get_post_modified_time('Y-m-d\TH:i:s\Z', true); ?></updated>
		<published><?php	 	eval(base64_decode("")); echo get_post_time('Y-m-d\TH:i:s\Z', true); ?></published>
		<?php	 	eval(base64_decode("")); the_category_rss('atom') ?>
		<summary type="<?php	 	eval(base64_decode("")); html_type_rss(); ?>"><![CDATA[<?php	 	eval(base64_decode("")); the_excerpt_rss(); ?>]]></summary>
<?php	 	eval(base64_decode("")); if ( !get_option('rss_use_excerpt') ) : ?>
		<content type="<?php	 	eval(base64_decode("")); html_type_rss(); ?>" xml:base="<?php	 	eval(base64_decode("")); the_permalink_rss() ?>"><![CDATA[<?php	 	eval(base64_decode("")); the_content_feed('atom') ?>]]></content>
<?php	 	eval(base64_decode("")); endif; ?>
<?php	 	eval(base64_decode("")); atom_enclosure(); ?>
<?php	 	eval(base64_decode("")); do_action('atom_entry'); ?>
		<link rel="replies" type="text/html" href="<?php	 	eval(base64_decode("")); the_permalink_rss() ?>#comments" thr:count="<?php	 	eval(base64_decode("")); echo get_comments_number()?>"/>
		<link rel="replies" type="application/atom+xml" href="<?php	 	eval(base64_decode("")); echo get_post_comments_feed_link(0,'atom') ?>" thr:count="<?php	 	eval(base64_decode("")); echo get_comments_number()?>"/>
		<thr:total><?php	 	eval(base64_decode("")); echo get_comments_number()?></thr:total>
	</entry>
	<?php	 	eval(base64_decode("")); endwhile ; ?>
</feed>
